import "./DestinationStyles.css";
import { Component } from "react";
import des1 from "../assets/des1.jpg";
import des2 from "../assets/des2.jpg";

class DestinationData extends Component {
  render() {
    return (
      <div className="first-des">
        <div className="des-text">
          <h2>{this.props.heading}</h2>
          <p>
            Experience the Sacred Heart of India: Mathura, Vrindavan, Agra, and
            Fatehpur Sikri
            <br />
            <b>1. Mathura:</b> The birthplace of Lord Krishna, Mathura exudes
            devotion in its bustling streets and vibrant temples, offering a
            spiritual journey during festivals like Janmashtami.
            <br />
            <b> 2. Vrindavan:</b> Immerse in the divine romance of Radha and
            Krishna amidst ancient temples resonating with bhajans and vibrant
            Holi celebrations.
            <br />
            <b>3. Agra:</b> Witness the eternal love embodied by the Taj Mahal,
            marvel at the majestic Agra Fort, and delve into the rich Mughal
            history and culture.
            <br />
            <b>4. Fatehpur Sikri:</b> Explore the deserted streets of Emperor
            Akbar's vision, adorned with magnificent structures like the Buland
            Darwaza and Jama Masjid, echoing tales of a glorious past.
          </p>
        </div>

        <div className="image">
          <img alt="des-img" src={des1} />
          <img alt="des-img" src={des2} />
        </div>
      </div>
    );
  }
}

export default DestinationData;
